package com.dbobjekts.codegen.metadata

import com.dbobjekts.AnyColumn
import com.dbobjekts.ColumnName
import com.dbobjekts.TableName

class DBAutoGeneratedKeyDefinition(table: TableName,
                                   columnName: ColumnName,
                                   columnType: AnyColumn,
                                   comment: String? = null)
   : DBColumnDefinition(table, columnName, columnType, comment), DBPrimaryKeyDefinition, DBGeneratedPrimaryKey {

    override fun prettyPrint(): String =
        "     Auto-generated column $tableName.$columnName maps to ${fullyQualifiedClassName()}."

    override fun toString(): String = columnName.value
}
